import React from "react";
import Gallery from "../components/Gallery";

function Home() {
  return (
    <div style={{ padding: "20px", maxWidth: "900px", margin: "0 auto" }}>
      {/* Welcome Section */}
      <section style={{ textAlign: "center", marginBottom: "40px" }}>
        <h1>Welcome to Mini Volunteering</h1>
        <p style={{ fontSize: "16px", color: "var(--muted)" }}>
          Discover local volunteering activities and join our community to make a difference!
        </p>
      </section>

      {/* Gallery Section */}
      <Gallery />

      {/* About Section */}
      <section style={{ marginTop: "50px", textAlign: "center" }}>
        <h2>Why Volunteering?</h2>
        <p style={{ fontSize: "15px", lineHeight: "1.6" }}>
          Volunteering helps communities grow, teaches new skills, and connects
          you with people who share your passion for giving back. Explore local
          activities and start making a difference today!
        </p>
      </section>

      {/* Optional CTA */}
      <section style={{ marginTop: "40px", textAlign: "center" }}>
        <button
          style={{
            padding: "10px 25px",
            fontSize: "16px",
            fontWeight: 600,
            borderRadius: "5px",
            border: "none",
            backgroundColor: "var(--primary)",
            color: "#fff",
            cursor: "pointer",
          }}
          onClick={() => window.location.href = "/list"}
        >
          View Activities
        </button>
      </section>
    </div>
  );
}

export default Home;
