import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addActivityAPI } from '../features/activitySlice';
import { useNavigate } from 'react-router-dom';

function AddActivity() {
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [date, setDate] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !desc || !date) {
      alert('Please fill all fields');
      return;
    }
    await dispatch(addActivityAPI({ title, description: desc, date }));
    navigate('/list');
  }

  return (
    <div className="container">
      <div className="header">
        <div>
          <h1>Add Activity</h1>
          <p>Fill details for the volunteering event.</p>
        </div>
      </div>

      <form className="form" onSubmit={handleSubmit}>
        <div className="row">
          <div style={{flex:1}}>
            <label>Title</label>
            <input className="input" value={title} onChange={e=>setTitle(e.target.value)} placeholder="Beach cleanup" />
          </div>
          <div style={{width:180}}>
            <label>Date</label>
            <input type="date" className="input" value={date} onChange={e=>setDate(e.target.value)} />
          </div>
        </div>

        <div style={{marginBottom:12}}>
          <label>Description</label>
          <textarea className="input" value={desc} onChange={e=>setDesc(e.target.value)} placeholder="Describe the activity..."></textarea>
        </div>

        <div className="form-actions">
          <button type="button" className="btn ghost" onClick={()=>navigate('/')}>Cancel</button>
          <button type="submit" className="btn primary">Create Activity</button>
        </div>
      </form>
    </div>
  );
}

export default AddActivity;
