import React from "react";
import Slider from "react-slick";
import "./Gallery.css"; // optional CSS for styling

function Gallery() {
  const photos = [
    { src: "/images/photo1.jpg", caption: "Community Cleanup" },
    { src: "/images/photo2.jpg", caption: "Tree Plantation" },
    { src: "/images/photo3.jpg", caption: "Beach Cleaning" },
    { src: "/images/photo4.jpg", caption: "Education Drive" },
    { src: "/images/photo5.jpg", caption: "Food Distribution" },
    { src: "/images/photo6.jpg", caption: "Awareness Campaign" },
    { src: "/images/photo7.jpg", caption: "Animal Care" },
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3500,
    arrows: true,
  };

  return (
    <div className="gallery-container">
      <h3>Gallery</h3>
      <Slider {...settings}>
        {photos.map((photo, index) => (
          <div key={index} className="gallery-slide">
            <img src={photo.src} alt={photo.caption} />
            <div className="caption">{photo.caption}</div>
          </div>
        ))}
      </Slider>
    </div>
  );
}

export default Gallery;
