import { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { updateActivityAPI } from '../features/activitySlice';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

function EditActivity() {
  const { id } = useParams();
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [date, setDate] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`/activities/${id}`).then(res => {
      setTitle(res.data.title);
      setDesc(res.data.description);
      setDate(res.data.date);
    }).catch(()=>alert('Failed to load activity'));
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await dispatch(updateActivityAPI({ id, data: { title, description: desc, date } }));
    navigate('/list');
  }

  return (
    <div className="container">
      <div className="header">
        <div>
          <h1>Edit Activity</h1>
          <p>Modify details and save.</p>
        </div>
      </div>

      <form className="form" onSubmit={handleSubmit}>
        <div className="row">
          <div style={{flex:1}}>
            <label>Title</label>
            <input className="input" value={title} onChange={e=>setTitle(e.target.value)} />
          </div>
          <div style={{width:180}}>
            <label>Date</label>
            <input type="date" className="input" value={date} onChange={e=>setDate(e.target.value)} />
          </div>
        </div>

        <div style={{marginBottom:12}}>
          <label>Description</label>
          <textarea className="input" value={desc} onChange={e=>setDesc(e.target.value)}></textarea>
        </div>

        <div className="form-actions">
          <button type="button" className="btn ghost" onClick={()=>navigate('/list')}>Cancel</button>
          <button type="submit" className="btn primary">Save Changes</button>
        </div>
      </form>
    </div>
  );
}

export default EditActivity;
