import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchActivities, deleteActivityAPI } from '../features/activitySlice';
import ActivityCard from '../components/ActivityCard';

function ActivityList() {
  const dispatch = useDispatch();
  const list = useSelector(state => state.activities.list);

  useEffect(() => {
    dispatch(fetchActivities());
  }, [dispatch]);

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this activity?')) return;
    dispatch(deleteActivityAPI(id));
  }

  return (
    <div className="container">
      <div className="header">
        <div>
          <h1>All Activities</h1>
          <p>{list.length} activities</p>
        </div>
        <div>
          <a href="/add" className="btn primary">+ Add Activity</a>
        </div>
      </div>

      {list.length === 0 ? (
        <div className="empty container" style={{maxWidth:720}}>
          <h3>No activities yet</h3>
          <p>Create one to get started.</p>
        </div>
      ) : (
        <div className="container" style={{padding:0}}>
          <div className="grid" style={{marginTop:12}}>
            {list.map(act => (
              <ActivityCard key={act._id} activity={act} onDelete={handleDelete} />
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default ActivityList;
